import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import '../App.css';
import axios from 'axios';

class showUserDetails extends Component {
    constructor(props) {
      super(props);
      this.state = {
        user: {}
      };
    }

    componentDidMount() {
        // console.log("Print id: " + this.props.match.params.id);
        axios
          .get('http://localhost:8082/routes/api/users/'+this.props.match.params.id)
          .then(res => {
            // console.log("Print-showUserDetails-API-response: " + res.data);
            this.setState({
              user: res.data
            })
          })
          .catch(err => {
            console.log("Error from ShowUserDetails");
          })
      };

      onDeleteClick (id) {
        axios
          .delete('http://localhost:8082/routes/api/users/'+id)
          .then(res => {
            this.props.history.push("/");
          })
          .catch(err => {
            console.log("Error form ShowUserDetails_deleteClick");
          })
      };

      render() {

        const user = this.state.user;
        let UserItem = <div>
          <table className="table table-hover table-dark">
            {/* <thead>
              <tr>
                <th scope="col">#</th>
                <th scope="col">First</th>
                <th scope="col">Last</th>
                <th scope="col">Handle</th>
              </tr>
            </thead> */}
            <tbody>
              <tr>
                <th scope="row">1</th>
                <td>Nom</td>
                <td>{ user.name }</td>
              </tr>
              <tr>
                <th scope="row">2</th>
                <td>Email</td>
                <td>{ user.email }</td>
              </tr>
              <tr>
                <th scope="row">3</th>
                <td>Date</td>
                <td>{ user.date }</td>
              </tr>
            </tbody>
          </table>
        </div>


}
};

export default showUserDetails;
