import BookCard from './userCard';
import '../App.css';

class ShowList extends Component {
    constructor(props) {
      super(props);
      this.state = {
        users: []
      };
    }
    componentDidMount() {
        axios
          .get('http://localhost:3000/routes/api/users/')
          .then(res => {
            this.setState({
              users: res.data
            })
          })
          .catch(err =>{
            console.log('error dans showList');
          })
      };

